-- File: trig_i_i_syst_enum_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst/api_views/syst_enum_functional_types/trig_i_i_syst_enum_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
DECLARE var_syst_defined boolean; BEGIN SELECT syst_defined INTO var_syst_defined FROM ms_syst_data.syst_enums WHERE id = new.enum_id;
IF var_syst_defined THEN RAISE EXCEPTION USING MESSAGE = 'The functional type creation for the requested ' || 'enumeration is invalid.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_i_syst_enum_functional_types' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
INSERT INTO ms_syst_data.syst_enum_functional_types ( internal_name , display_name , external_name , enum_id , syst_description , user_description) VALUES ( new.internal_name , new.display_name , new.external_name , new.enum_id , '(System Description Not Available)' , new.user_description) RETURNING INTO new id , internal_name , display_name , external_name , var_syst_defined , enum_id , syst_description , user_description , diag_timestamp_created , diag_role_created , diag_timestamp_modified , diag_wallclock_modified , diag_role_modified , diag_row_version , diag_update_count ;
RETURN new;
END;

-- File: trig_i_i_syst_enum_items.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst/api_views/syst_enum_items/trig_i_i_syst_enum_items.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF exists( SELECT TRUE FROM ms_syst_data.syst_enums WHERE id = new.enum_id AND syst_defined AND NOT user_maintainable) THEN RAISE EXCEPTION USING MESSAGE = 'You may not add new enumeration items for system defined ' || 'enumerations that are not marked user maintainable.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_i_syst_enum_items' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
INSERT INTO ms_syst_data.syst_enum_items ( internal_name , display_name , external_name , enum_id , functional_type_id , enum_default , functional_type_default , syst_defined , user_maintainable , syst_description , user_description , sort_order , user_options ) VALUES ( new.internal_name , new.display_name , new.external_name , new.enum_id , new.functional_type_id , new.enum_default , new.functional_type_default , FALSE , TRUE , '(System Description Not Available)' , new.user_description , new.sort_order , new.user_options ) RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_i_syst_enums.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst/api_views/syst_enums/trig_i_i_syst_enums.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
INSERT INTO ms_syst_data.syst_enums ( internal_name , display_name , syst_description , user_description , syst_defined , user_maintainable , default_user_options ) VALUES ( new.internal_name , new.display_name , '(System Description Not Available)' , new.user_description , FALSE , TRUE , new.default_user_options ) RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_u_syst_enum_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst/api_views/syst_enum_functional_types/trig_i_u_syst_enum_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN IF (old.syst_defined AND old.internal_name != new.internal_name) OR old.enum_id != new.enum_id THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_enum_functional_types' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_enum_functional_types SET internal_name = new.internal_name , display_name = new.display_name , external_name = new.external_name , user_description = new.user_description WHERE id = new.id RETURNING INTO new id , internal_name , display_name , external_name , old.syst_defined , enum_id , syst_description , user_description , diag_timestamp_created , diag_role_created , diag_timestamp_modified , diag_wallclock_modified , diag_role_modified , diag_row_version , diag_update_count ;
RAISE WARNING '======>>>>>>> New Func. Type: %', to_jsonb(new);
RETURN new;
END;

-- File: trig_i_u_syst_enum_items.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst/api_views/syst_enum_items/trig_i_u_syst_enum_items.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF old.syst_defined AND (new.internal_name != old.internal_name OR new.functional_type_id != old.functional_type_id) THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_enum_items' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_enum_items SET internal_name = new.internal_name , display_name = new.display_name , external_name = new.external_name , functional_type_id = new.functional_type_id , enum_default = new.enum_default , functional_type_default = new.functional_type_default , user_description = new.user_description , sort_order = new.sort_order , user_options = new.user_options WHERE id = new.id RETURNING * INTO new;
RETURN new;
END;

