IF old.syst_defined AND new.internal_name != old.internal_name THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_enums' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_enums SET internal_name = new.internal_name , display_name = new.display_name , user_description = new.user_description , default_user_options = new.default_user_options WHERE id = new.id RETURNING * INTO new;
