routine.definition:
-- File: trig_a_iu_syst_enum_items_maintain_sort_order.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_a_iu_syst_enum_items_maintain_sort_order.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
UPDATE ms_syst_data.syst_enum_items SET sort_order = sort_order + 1 WHERE enum_id = new.enum_id AND sort_order = new.sort_order AND id != new.id;
RETURN NULL;
END;

-- File: trig_b_i_syst_enum_functional_type_validate_new_allowed.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_functional_types/trig_b_i_syst_enum_functional_type_validate_new_allowed.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF NOT EXISTS(SELECT TRUE FROM ms_syst_data.syst_enum_functional_types WHERE enum_id = new.enum_id) AND EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_items WHERE enum_id = new.enum_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'You cannot add a functional type requirement after enumeration item ' || 'records have already been defined.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_i_syst_enum_functional_type_validate_new_allowed' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new;
END;

-- File: trig_b_i_syst_enum_items_maintain_sort_order.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_i_syst_enum_items_maintain_sort_order.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
new.sort_order := coalesce( new.sort_order, ( SELECT max( sort_order ) + 1 FROM ms_syst_data.syst_enum_items WHERE enum_id = new.enum_id ), 1 );
RETURN new;
END;

-- File: trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.enum_id = new.enum_id) AND NOT EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.id = new.functional_type_id AND seft.enum_id = new.enum_id) THEN
RAISE EXCEPTION USING MESSAGE = 'The enumeration requires a valid functional type to be specified.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_iu_syst_enum_items_validate_functional_types' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new; END;
