routine.definition:
-- File: trig_a_iu_syst_enum_items_maintain_sort_order.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_a_iu_syst_enum_items_maintain_sort_order.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
UPDATE ms_syst_data.syst_enum_items SET sort_order = sort_order + 1 WHERE enum_id = new.enum_id AND sort_order = new.sort_order AND id != new.id;
RETURN NULL;
END;

-- File: trig_b_d_syst_hierarchies_validate_prereqs.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchies/trig_b_d_syst_hierarchies_validate_prereqs.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF ms_syst_priv.is_hierarchy_record_referenced( old.id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy records may not be deleted when they ' || 'are referenced in the data of hierarchy ' || 'implementing Components.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_d_syst_hierarchies_validate_prereqs' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( old ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
END;

-- File: trig_b_d_syst_hierarchy_items_hierarchy_inactive_check.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_d_syst_hierarchy_items_hierarchy_inactive_check.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF ( SELECT eft.internal_name = 'hierarchy_states_active' FROM ms_syst_data.syst_hierarchies ch JOIN ms_syst_data.syst_enum_items ei ON ei.id = ch.hierarchy_state_id JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ch.id = old.hierarchy_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Deleting Hierarchy Item records may only be done ' || 'when the parent Hierarchy is in an "inactive" ' || 'state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_d_syst_hierarchy_items_hierarchy_inactive_check' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( old ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN old;
END;

-- File: trig_b_i_syst_enum_functional_type_validate_new_allowed.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_functional_types/trig_b_i_syst_enum_functional_type_validate_new_allowed.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF NOT EXISTS(SELECT TRUE FROM ms_syst_data.syst_enum_functional_types WHERE enum_id = new.enum_id) AND EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_items WHERE enum_id = new.enum_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'You cannot add a functional type requirement after enumeration item ' || 'records have already been defined.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_i_syst_enum_functional_type_validate_new_allowed' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new;
END;

-- File: trig_b_i_syst_enum_items_maintain_sort_order.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_i_syst_enum_items_maintain_sort_order.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
new.sort_order := coalesce( new.sort_order, ( SELECT max( sort_order ) + 1 FROM ms_syst_data.syst_enum_items WHERE enum_id = new.enum_id ), 1 );
RETURN new;
END;

-- File: trig_b_i_syst_hierarchies_validate_inactive.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchies/trig_b_i_syst_hierarchies_validate_inactive.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF ( SELECT eft.internal_name = 'hierarchy_states_active' FROM ms_syst_data.syst_enum_items ei JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ei.id = new.hierarchy_state_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'New Hierarchy records may not be inserted using ' || 'an "active" state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_i_syst_hierarchies_validate_inactive' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( new ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new;
END;

-- File: trig_b_i_syst_hierarchy_items_hierarchy_prereqs.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_i_syst_hierarchy_items_hierarchy_prereqs.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
DECLARE
var_context_data record;
BEGIN
SELECT INTO var_context_data eft.internal_name = 'hierarchy_states_active' AS is_active , ch.structured FROM ms_syst_data.syst_hierarchies ch JOIN ms_syst_data.syst_enum_items ei ON ei.id = ch.hierarchy_state_id JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ch.id = new.hierarchy_id;
CASE WHEN var_context_data.is_active THEN
RAISE EXCEPTION USING MESSAGE = 'Inserting Hierarchy Item records may only be ' || 'done when the parent Hierarchy is in an ' || '"inactive" state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_i_syst_hierarchy_items_hierarchy_prereqs' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( new ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
WHEN NOT var_context_data.structured THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy Item records may not be inserted ' || 'for unstructured Hierarchies.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_i_syst_hierarchy_items_hierarchy_prereqs' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( new ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END CASE;
END;

-- File: trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.enum_id = new.enum_id) AND NOT EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.id = new.functional_type_id AND seft.enum_id = new.enum_id) THEN
RAISE EXCEPTION USING MESSAGE = 'The enumeration requires a valid functional type to be specified.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_iu_syst_enum_items_validate_functional_types' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new; END;

-- File: trig_b_iu_syst_hierarchy_items_depth_maint.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_iu_syst_hierarchy_items_depth_maint.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
CASE WHEN tg_op = 'INSERT' AND new.hierarchy_depth IS NULL THEN
new.hierarchy_depth := coalesce( ( SELECT max( hierarchy_depth ) + 1 FROM ms_syst_data.syst_hierarchy_items WHERE hierarchy_id = new.hierarchy_id ), 1 );
WHEN exists( SELECT TRUE FROM ms_syst_data.syst_hierarchy_items WHERE hierarchy_id = new.hierarchy_id AND hierarchy_depth = new.hierarchy_depth ) THEN
UPDATE ms_syst_data.syst_hierarchy_items SET hierarchy_depth = hierarchy_depth + 1 WHERE hierarchy_id = new.hierarchy_id AND hierarchy_depth = new.hierarchy_depth;
ELSE NULL; END CASE;
RETURN new;
END;

-- File: trig_b_u_syst_hierarchies_validate_state_change.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchies/trig_b_u_syst_hierarchies_validate_state_change.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
DECLARE
var_context_data record;
BEGIN
IF old.hierarchy_state_id = new.hierarchy_state_id THEN RETURN new; END IF;
SELECT INTO var_context_data eft.internal_name AS new_state_name FROM ms_syst_data.syst_enum_items ei JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ei.id = new.hierarchy_state_id;
CASE var_context_data.new_state_name WHEN 'hierarchy_states_active' THEN
IF NOT ms_syst_priv.is_hierarchy_config_valid( new ) THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy records may not be set to an ' || '"active" state unless the Hierarchy record and ' || 'all associated Hierarchy Item records are ' || 'consistent and valid.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_state_change' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END IF;
WHEN 'hierarchy_states_inactive' THEN
IF ms_appl.is_hierarchy_record_referenced( new.id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy records may not be set to an ' || '"inactive" state when the data of hierarchy ' || 'implementing Components still reference the ' || 'Hierarchy record.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_state_change' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END IF;
END CASE;
END;

-- File: trig_b_u_syst_hierarchies_validate_structured.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchies/trig_b_u_syst_hierarchies_validate_structured.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
CASE WHEN new.structured != old.structured AND ( SELECT eft.internal_name = 'hierarchy_states_active' FROM ms_syst_data.syst_enum_items ei JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ei.id = new.hierarchy_state_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'The "structured" value of the Hierarchy may ' || 'only be changed when the Hierarchy is in an ' || '"inactive" state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_structured' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
WHEN old.structured AND NOT new.structured AND exists( SELECT TRUE FROM ms_syst_data.syst_hierarchy_items chi WHERE chi.hierarchy_id = new.id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Unstructured Hierarchies should not have ' || 'structuring Hierarchy Item records.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_structured' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END CASE;
END;

-- File: trig_b_u_syst_hierarchy_items_hierarchy_inactive_check.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_u_syst_hierarchy_items_hierarchy_inactive_check.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF (new.hierarchy_depth != old.hierarchy_depth OR new.required != old.required OR new.allow_leaf_nodes != old.allow_leaf_nodes) AND ( SELECT eft.internal_name = 'hierarchy_states_active' FROM ms_syst_data.syst_hierarchies ch JOIN ms_syst_data.syst_enum_items ei ON ei.id = ch.hierarchy_state_id JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ch.id = new.hierarchy_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Deleting Hierarchy Item records may only be done ' || 'when the parent Hierarchy is in an "inactive" ' || 'state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_d_syst_hierarchy_items_hierarchy_inactive_check' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new;
END;
