routine.definition:
-- File: trig_a_i_syst_instance_type_apps_create_inst_type_contexts.eex.sql -- Location: musebms/database/components/system/mscmp_syst_instance/ms_syst_data/syst_instance_type_applications/trig_a_i_syst_instance_type_apps_create_inst_type_contexts.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
INSERT INTO ms_syst_data.syst_instance_type_contexts ( instance_type_application_id, application_context_id, default_db_pool_size ) SELECT new.id , id , 0 FROM ms_syst_data.syst_application_contexts WHERE application_id = new.application_id;
RETURN new; END;

-- File: trig_a_i_syst_instances_create_instance_contexts.eex.sql -- Location: musebms/database/components/system/mscmp_syst_instance/ms_syst_data/syst_instances/trig_a_i_syst_instances_create_instance_contexts.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
INSERT INTO ms_syst_data.syst_instance_contexts ( internal_name , instance_id , application_context_id , start_context , db_pool_size , context_code ) SELECT new.internal_name || '_' || sac.internal_name , new.id , sac.id , sac.login_context , sitc.default_db_pool_size , public.gen_random_bytes( 16 ) FROM ms_syst_data.syst_owners so, ms_syst_data.syst_application_contexts sac JOIN ms_syst_data.syst_instance_type_contexts sitc ON sitc.application_context_id = sac.id JOIN ms_syst_data.syst_applications sa ON sa.id = sac.application_id JOIN ms_syst_data.syst_instance_type_applications sita ON sita.id = sitc.instance_type_application_id WHERE so.id = new.owner_id AND sita.instance_type_id = new.instance_type_id AND sa.id = new.application_id;
RETURN new;
END;

-- File: trig_a_iu_syst_enum_items_maintain_sort_order.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_a_iu_syst_enum_items_maintain_sort_order.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
UPDATE ms_syst_data.syst_enum_items SET sort_order = sort_order + 1 WHERE enum_id = new.enum_id AND sort_order = new.sort_order AND id != new.id;
RETURN NULL;
END;

-- File: trig_b_d_syst_applications_delete_contexts.eex.sql -- Location: musebms/database/components/system/mscmp_syst_instance/ms_syst_data/syst_applications/trig_b_d_syst_applications_delete_contexts.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
ALTER TABLE ms_syst_data.syst_application_contexts DISABLE TRIGGER c50_trig_b_d_syst_application_contexts_validate_owner_context;
DELETE FROM ms_syst_data.syst_application_contexts WHERE application_id = old.id;
ALTER TABLE ms_syst_data.syst_application_contexts ENABLE TRIGGER c50_trig_b_d_syst_application_contexts_validate_owner_context;
RETURN old;
END;

-- File: trig_b_i_syst_enum_functional_type_validate_new_allowed.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_functional_types/trig_b_i_syst_enum_functional_type_validate_new_allowed.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF NOT EXISTS(SELECT TRUE FROM ms_syst_data.syst_enum_functional_types WHERE enum_id = new.enum_id) AND EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_items WHERE enum_id = new.enum_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'You cannot add a functional type requirement after enumeration item ' || 'records have already been defined.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_i_syst_enum_functional_type_validate_new_allowed' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new;
END;

-- File: trig_b_i_syst_enum_items_maintain_sort_order.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_i_syst_enum_items_maintain_sort_order.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
new.sort_order := coalesce( new.sort_order, ( SELECT max( sort_order ) + 1 FROM ms_syst_data.syst_enum_items WHERE enum_id = new.enum_id ), 1 );
RETURN new;
END;

-- File: trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.enum_id = new.enum_id) AND NOT EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.id = new.functional_type_id AND seft.enum_id = new.enum_id) THEN
RAISE EXCEPTION USING MESSAGE = 'The enumeration requires a valid functional type to be specified.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_iu_syst_enum_items_validate_functional_types' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new; END;

-- File: trig_b_iud_syst_application_contexts_validate_owner_context.eex.sql -- Location: musebms/database/components/system/mscmp_syst_instance/ms_syst_data/syst_application_contexts/trig_b_iud_syst_application_contexts_validate_owner_context.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF tg_op = 'DELETE' THEN
IF old.database_owner_context THEN RAISE EXCEPTION USING MESSAGE = 'You may not delete the designated database owner ' || 'context for an Application from the database.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
RETURN old;
END IF;
IF tg_op = 'UPDATE' THEN
IF new.database_owner_context != old.database_owner_context THEN RAISE EXCEPTION USING MESSAGE = 'The database owner context designation may ' || 'only be set on INSERT.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
END IF;
IF tg_op IN ('INSERT', 'UPDATE') THEN
-- There may only be one database owner context for any one application. IF new.database_owner_context AND exists( SELECT TRUE FROM ms_syst_data.syst_application_contexts sac WHERE sac.application_id = new.application_id AND sac.id != new.id AND sac.database_owner_context) THEN RAISE EXCEPTION USING MESSAGE = 'Each Application may only have one Application Context ' || 'defined as being the database owner.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
-- Database context owners may not be login contexts nor may they be -- started IF new.database_owner_context AND (new.login_context OR new.start_context) THEN RAISE EXCEPTION USING MESSAGE = 'A database owner context may not be a login ' || 'context nor may it be started.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
RETURN new;
END IF;
END;
