-- File: trig_i_i_syst_perm_role_grants.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perm_role_grants/trig_i_i_syst_perm_role_grants.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF ( SELECT syst_defined FROM ms_syst_data.syst_perm_roles WHERE id = new.perm_role_id ) THEN RAISE EXCEPTION USING MESSAGE = 'You may not create new permission role grant ' || 'records for system defined permission roles ' || 'using this API view.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_i_syst_perm_role_grants' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
INSERT INTO ms_syst_data.syst_perm_role_grants ( perm_role_id , perm_id , view_scope , maint_scope , admin_scope , ops_scope ) VALUES ( new.perm_role_id , new.perm_id , new.view_scope , new.maint_scope , new.admin_scope , new.ops_scope ) RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_i_syst_perm_roles.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perm_roles/trig_i_i_syst_perm_roles.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
INSERT INTO ms_syst_data.syst_perm_roles ( internal_name , display_name , perm_functional_type_id , syst_defined , syst_description , user_description ) VALUES ( new.internal_name , new.display_name , new.perm_functional_type_id , FALSE , '(System Description Not Available)' , new.user_description ) RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_i_syst_perms.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perms/trig_i_i_syst_perms.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
INSERT INTO ms_syst_data.syst_perms ( internal_name , display_name , perm_functional_type_id , syst_defined , syst_description , user_description , view_scope_options , maint_scope_options , admin_scope_options , ops_scope_options ) VALUES ( new.internal_name , new.display_name , new.perm_functional_type_id , FALSE , '(System Description Not Available)' , new.user_description , coalesce(new.view_scope_options, array['unused']) , coalesce(new.maint_scope_options, array['unused']) , coalesce(new.admin_scope_options, array['unused']) , coalesce(new.ops_scope_options, array['unused']) ) RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_u_syst_perm_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perm_functional_types/trig_i_u_syst_perm_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF new.internal_name != old.internal_name THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_perm_functional_types' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_perm_functional_types SET display_name = new.display_name , user_description = new.user_description WHERE id = new.id RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_u_syst_perm_role_grants.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perm_role_grants/trig_i_u_syst_perm_role_grants.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF ( SELECT syst_defined FROM ms_syst_data.syst_perm_roles WHERE id = new.perm_role_id ) OR new.perm_role_id != old.perm_role_id OR new.perm_id != old.perm_id THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_perm_role_grants' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_perm_role_grants SET view_scope = new.view_scope , maint_scope = new.maint_scope , admin_scope = new.admin_scope , ops_scope = new.ops_scope WHERE id = new.id RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_u_syst_perm_roles.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perm_roles/trig_i_u_syst_perm_roles.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF (old.syst_defined AND new.internal_name != old.internal_name) OR new.syst_defined != old.syst_defined OR new.perm_functional_type_id != old.perm_functional_type_id THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_perm_roles' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_perm_roles SET internal_name = new.internal_name , display_name = new.display_name , user_description = new.user_description WHERE id = new.id RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_u_syst_perms.eex.sql -- Location: musebms/database/components/system/mscmp_syst_perms/ms_syst/api_views/syst_perms/trig_i_u_syst_perms.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF (old.syst_defined AND (new.internal_name != old.internal_name OR new.view_scope_options != old.view_scope_options OR new.maint_scope_options != old.maint_scope_options OR new.admin_scope_options != old.admin_scope_options OR new.ops_scope_options != old.ops_scope_options )) OR new.syst_defined != old.syst_defined OR new.perm_functional_type_id != old.perm_functional_type_id THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_perms' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_perms SET internal_name = new.internal_name , display_name = new.display_name , user_description = new.user_description , view_scope_options = new.view_scope_options , maint_scope_options = new.maint_scope_options , admin_scope_options = new.admin_scope_options , ops_scope_options = new.ops_scope_options WHERE id = new.id RETURNING * INTO new;
RETURN new;
END;
