routine.definition:
-- File: trig_i_d_syst_sessions.eex.sql -- Location: musebms/database/components/system/mscmp_syst_session/ms_syst/api_views/syst_sessions/trig_i_d_syst_sessions.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Â© Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
DELETE FROM ms_syst_data.syst_sessions WHERE id = old.id RETURNING * INTO old;
RETURN old;
END;

-- File: trig_i_i_syst_sessions.eex.sql -- Location: musebms/database/components/system/mscmp_syst_session/ms_syst/api_views/syst_sessions/trig_i_i_syst_sessions.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Â© Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
INSERT INTO ms_syst_data.syst_sessions ( internal_name, session_data, session_expires ) VALUES ( new.internal_name, new.session_data, new.session_expires ) RETURNING * INTO new;
RETURN new;
END;

-- File: trig_i_u_syst_sessions.eex.sql -- Location: musebms/database/components/system/mscmp_syst_session/ms_syst/api_views/syst_sessions/trig_i_u_syst_sessions.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Â© Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
CASE WHEN new.internal_name != old.internal_name THEN
RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_sessions' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name;
WHEN new.session_expires < now() THEN
RAISE EXCEPTION USING MESSAGE = 'The syst_sessions record may not be updated after the ' || 'session_expires date/time is past.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_sessions' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
UPDATE ms_syst_data.syst_sessions SET session_data = new.session_data , session_expires = new.session_expires WHERE id = new.id RETURNING * INTO new;
RETURN new;
END CASE;
END;
