-- File: trig_i_d_syst_hierarchies.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst/api_views/syst_hierarchies/trig_i_d_syst_hierarchies.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF old.syst_defined THEN
RAISE EXCEPTION USING MESSAGE = 'You cannot delete a system defined Hierarchy using the API Views.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl' ,p_proc_name => 'trig_i_d_syst_hierarchies' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
DELETE FROM ms_syst_data.syst_hierarchies WHERE id = old.id RETURNING * INTO old;
RETURN old;
END;

-- File: trig_i_d_syst_hierarchy_items.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst/api_views/syst_hierarchy_items/trig_i_d_syst_hierarchy_items.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright © Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF ( SELECT syst_defined AND NOT user_maintainable FROM ms_syst_data.syst_hierarchies WHERE id = old.hierarchy_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'You cannot delete a non-user maintainable system ' || 'defined Hierarchy Item using the API Views.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl' ,p_proc_name => 'trig_i_d_syst_hierarchy_items' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
IF ( SELECT count( 1 ) > 1 AND max( hierarchy_depth ) > old.hierarchy_depth FROM ms_syst_data.syst_hierarchy_items WHERE hierarchy_id = old.hierarchy_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'You may only delete the Hierarchy Item record ' || 'representing the lowest level of the hierarchy ' || 'using this API view.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl' ,p_proc_name => 'trig_i_d_syst_hierarchy_items' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
DELETE FROM ms_syst_data.syst_hierarchy_items WHERE id = old.id RETURNING * INTO OLD;
RETURN old;
END;

