UPDATE ms_syst_data.syst_owner_network_rules SET ordering = new.ordering , functional_type = new.functional_type , ip_host_or_network = new.ip_host_or_network , ip_host_range_lower = new.ip_host_range_lower , ip_host_range_upper = new.ip_host_range_upper WHERE id = new.id RETURNING id , owner_id , ordering , functional_type , ip_host_or_network , ip_host_range_lower , ip_host_range_upper , family( coalesce( ip_host_or_network, ip_host_range_lower ) ) AS ip_family , diag_timestamp_created , diag_role_created , diag_timestamp_modified , diag_wallclock_modified , diag_role_modified , diag_row_version , diag_update_count INTO new;
RETURN new;
END;

