IF new.owner_id != old.owner_id OR new.ip_family != old.ip_family THEN RAISE EXCEPTION USING MESSAGE = 'The requested data update included changes to fields disallowed ' || 'by the business rules of the API View.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_i_u_syst_owner_network_rules' ,p_exception_name => 'invalid_api_view_call' ,p_errcode => 'PM008' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
UPDATE ms_syst_data.syst_owner_network_rules SET ordering = new.ordering , functional_type = new.functional_type , ip_host_or_network = new.ip_host_or_network , ip_host_range_lower = new.ip_host_range_lower , ip_host_range_upper = new.ip_host_range_upper WHERE id = new.id RETURNING id , owner_id , ordering , functional_type , ip_host_or_network , ip_host_range_lower , ip_host_range_upper , family( coalesce( ip_host_or_network, ip_host_range_lower ) ) AS ip_family , diag_timestamp_created , diag_role_created , diag_timestamp_modified , diag_wallclock_modified , diag_role_modified , diag_row_version , diag_update_count INTO new;
RETURN new;
END;

