RAISE EXCEPTION USING MESSAGE = 'New Hierarchy records may not be inserted using ' || 'an "active" state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_i_syst_hierarchies_validate_inactive' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( new ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
