-- File: trig_b_i_syst_hierarchy_items_hierarchy_prereqs.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_i_syst_hierarchy_items_hierarchy_prereqs.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
DECLARE
var_context_data record;
BEGIN
SELECT INTO var_context_data eft.internal_name = 'hierarchy_states_active' AS is_active , ch.structured FROM ms_syst_data.syst_hierarchies ch JOIN ms_syst_data.syst_enum_items ei ON ei.id = ch.hierarchy_state_id JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ch.id = new.hierarchy_id;
CASE WHEN var_context_data.is_active THEN
RAISE EXCEPTION USING MESSAGE = 'Inserting Hierarchy Item records may only be ' || 'done when the parent Hierarchy is in an ' || '"inactive" state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_i_syst_hierarchy_items_hierarchy_prereqs' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( new ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
WHEN NOT var_context_data.structured THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy Item records may not be inserted ' || 'for unstructured Hierarchies.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_i_syst_hierarchy_items_hierarchy_prereqs' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => to_jsonb( new ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END CASE;
END;

-- File: trig_b_i_syst_identities_check_uniqueness.eex.sql -- Location: musebms/database/components/system/mscmp_syst_authn/ms_syst_data/syst_identities/trig_b_i_syst_identities_check_uniqueness.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF exists( SELECT TRUE FROM ms_syst_data.syst_access_accounts saa_this LEFT JOIN ms_syst_data.syst_access_accounts saa_other ON saa_this.owning_owner_id IS NOT DISTINCT FROM saa_other.owning_owner_id AND saa_this.id IS DISTINCT FROM saa_other.id LEFT JOIN ms_syst_data.syst_identities si_any ON ( si_any.access_account_id = saa_other.id OR si_any.access_account_id = saa_this.id ) AND si_any.identity_type_id = new.identity_type_id WHERE saa_this.id = new.access_account_id AND si_any.account_identifier = new.account_identifier ) THEN
RAISE EXCEPTION USING MESSAGE = 'The identity already matches that of a different access account ' 'in the same scope of identity resolution.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_i_syst_identities_validate_uniqueness' ,p_exception_name => 'duplicate_identity' ,p_errcode => 'PM002' ,p_param_data => jsonb_build_object( 'access_account_id', new.access_account_id ,'account_identifier', new.account_identifier ) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM002', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN NEW;
END;

-- File: trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Location: musebms/database/components/system/mscmp_syst_enums/ms_syst_data/syst_enum_items/trig_b_iu_syst_enum_items_validate_functional_types.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.enum_id = new.enum_id) AND NOT EXISTS( SELECT TRUE FROM ms_syst_data.syst_enum_functional_types seft WHERE seft.id = new.functional_type_id AND seft.enum_id = new.enum_id) THEN
RAISE EXCEPTION USING MESSAGE = 'The enumeration requires a valid functional type to be specified.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_iu_syst_enum_items_validate_functional_types' ,p_exception_name => 'invalid_functional_type' ,p_errcode => 'PM003' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new; END;

-- File: trig_b_iu_syst_hierarchy_items_depth_maint.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_iu_syst_hierarchy_items_depth_maint.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
CASE WHEN tg_op = 'INSERT' AND new.hierarchy_depth IS NULL THEN
new.hierarchy_depth := coalesce( ( SELECT max( hierarchy_depth ) + 1 FROM ms_syst_data.syst_hierarchy_items WHERE hierarchy_id = new.hierarchy_id ), 1 );
WHEN exists( SELECT TRUE FROM ms_syst_data.syst_hierarchy_items WHERE hierarchy_id = new.hierarchy_id AND hierarchy_depth = new.hierarchy_depth ) THEN
UPDATE ms_syst_data.syst_hierarchy_items SET hierarchy_depth = hierarchy_depth + 1 WHERE hierarchy_id = new.hierarchy_id AND hierarchy_depth = new.hierarchy_depth;
ELSE NULL; END CASE;
RETURN new;
END;

-- File:        trig_b_iu_syst_perm_role_grants_default_scopes.eex.sql
-- Location:    musebms/database/components/system/mscmp_syst_perms/ms_syst_data/syst_perm_role_grants/trig_b_iu_syst_perm_role_grants_default_scopes.eex.sql
