DECLARE
    var_perm ms_syst_data.syst_perms;

BEGIN

    SELECT * INTO STRICT var_perm FROM ms_syst_data.syst_perms WHERE id = new.perm_id;

    IF new.view_scope IS NULL THEN
        new.view_scope = var_perm.view_scope_options[1];
    END IF;

    IF new.maint_scope IS NULL THEN
        new.maint_scope = var_perm.maint_scope_options[1];
    END IF;

    IF new.admin_scope IS NULL THEN
        new.admin_scope = var_perm.admin_scope_options[1];
    END IF;

    IF new.ops_scope IS NULL THEN
        new.ops_scope = var_perm.ops_scope_options[1];
    END IF;

    RETURN new;

END;

-- File: trig_b_iud_syst_application_contexts_validate_owner_context.eex.sql -- Location: musebms/database/components/system/mscmp_syst_instance/ms_syst_data/syst_application_contexts/trig_b_iud_syst_application_contexts_validate_owner_context.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF tg_op = 'DELETE' THEN
IF old.database_owner_context THEN RAISE EXCEPTION USING MESSAGE = 'You may not delete the designated database owner ' || 'context for an Application from the database.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst_data' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
RETURN old;
END IF;
IF tg_op = 'UPDATE' THEN
IF new.database_owner_context != old.database_owner_context THEN RAISE EXCEPTION USING MESSAGE = 'The database owner context designation may ' || 'only be set on INSERT.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
END IF;
IF tg_op IN ('INSERT', 'UPDATE') THEN
-- There may only be one database owner context for any one application. IF new.database_owner_context AND exists( SELECT TRUE FROM ms_syst_data.syst_application_contexts sac WHERE sac.application_id = new.application_id AND sac.id != new.id AND sac.database_owner_context) THEN RAISE EXCEPTION USING MESSAGE = 'Each Application may only have one Application Context ' || 'defined as being the database owner.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
-- Database context owners may not be login contexts nor may they be -- started IF new.database_owner_context AND (new.login_context OR new.start_context) THEN RAISE EXCEPTION USING MESSAGE = 'A database owner context may not be a login ' || 'context nor may it be started.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_syst' ,p_proc_name => 'trig_b_iud_syst_application_contexts_validate_owner_context' ,p_exception_name => 'invalid_duplicate' ,p_errcode => 'PM008' ,p_param_data => to_jsonb(new) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM008', SCHEMA = tg_table_schema, TABLE = tg_table_name; END IF;
RETURN new;
END IF;
END;

-- File: trig_b_u_syst_hierarchies_validate_state_change.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchies/trig_b_u_syst_hierarchies_validate_state_change.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
DECLARE
var_context_data record;
BEGIN
IF old.hierarchy_state_id = new.hierarchy_state_id THEN RETURN new; END IF;
SELECT INTO var_context_data eft.internal_name AS new_state_name FROM ms_syst_data.syst_enum_items ei JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ei.id = new.hierarchy_state_id;
CASE var_context_data.new_state_name WHEN 'hierarchy_states_active' THEN
IF NOT ms_syst_priv.is_hierarchy_config_valid( new ) THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy records may not be set to an ' || '"active" state unless the Hierarchy record and ' || 'all associated Hierarchy Item records are ' || 'consistent and valid.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_state_change' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END IF;
WHEN 'hierarchy_states_inactive' THEN
IF ms_appl.is_hierarchy_record_referenced( new.id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Hierarchy records may not be set to an ' || '"inactive" state when the data of hierarchy ' || 'implementing Components still reference the ' || 'Hierarchy record.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_state_change' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END IF;
END CASE;
END;

-- File: trig_b_u_syst_hierarchies_validate_structured.eex.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchies/trig_b_u_syst_hierarchies_validate_structured.eex.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
CASE WHEN new.structured != old.structured AND ( SELECT eft.internal_name = 'hierarchy_states_active' FROM ms_syst_data.syst_enum_items ei JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ei.id = new.hierarchy_state_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'The "structured" value of the Hierarchy may ' || 'only be changed when the Hierarchy is in an ' || '"inactive" state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_structured' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
WHEN old.structured AND NOT new.structured AND exists( SELECT TRUE FROM ms_syst_data.syst_hierarchy_items chi WHERE chi.hierarchy_id = new.id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Unstructured Hierarchies should not have ' || 'structuring Hierarchy Item records.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_u_syst_hierarchies_validate_structured' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
ELSE
RETURN new;
END CASE;
END;

-- File: trig_b_u_syst_hierarchy_items_hierarchy_inactive_check.sql -- Location: musebms/database/components/system/mscmp_syst_hierarchy/ms_syst_data/syst_hierarchy_items/trig_b_u_syst_hierarchy_items_hierarchy_inactive_check.sql -- Project: Muse Systems Business Management System -- -- Copyright Lima Buttgereit Holdings LLC d/b/a Muse Systems -- This file may include content copyrighted and licensed from third parties. -- -- See the LICENSE file in the project root for license terms and conditions. -- See the NOTICE file in the project root for copyright ownership information. -- -- muse.information@musesystems.com :: https://muse.systems
BEGIN
IF (new.hierarchy_depth != old.hierarchy_depth OR new.required != old.required OR new.allow_leaf_nodes != old.allow_leaf_nodes) AND ( SELECT eft.internal_name = 'hierarchy_states_active' FROM ms_syst_data.syst_hierarchies ch JOIN ms_syst_data.syst_enum_items ei ON ei.id = ch.hierarchy_state_id JOIN ms_syst_data.syst_enum_functional_types eft ON eft.id = ei.functional_type_id WHERE ch.id = new.hierarchy_id ) THEN
RAISE EXCEPTION USING MESSAGE = 'Deleting Hierarchy Item records may only be done ' || 'when the parent Hierarchy is in an "inactive" ' || 'state.', DETAIL = ms_syst_priv.get_exception_details( p_proc_schema => 'ms_appl_data' ,p_proc_name => 'trig_b_d_syst_hierarchy_items_hierarchy_inactive_check' ,p_exception_name => 'invalid_state' ,p_errcode => 'PM003' ,p_param_data => jsonb_build_object('new', new, 'old', old) ,p_context_data => jsonb_build_object( 'tg_op', tg_op ,'tg_when', tg_when ,'tg_schema', tg_table_schema ,'tg_table_name', tg_table_name)), ERRCODE = 'PM003', SCHEMA = tg_table_schema, TABLE = tg_table_name;
END IF;
RETURN new;
END;
